mapper.select:
select

  distinct


from biz_station_target

  


  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

SELECT COUNT(*) FROM biz_station_target tt,biz_station st,biz_district dt where tt.station_code = st.station_code and dt.district_code = st.district_code
and dt.district_code = '${stationTarget.districtCode}'
and tt.year_month = '${stationTarget.yearMonth}'
and tt.accommodation_subsidy > ${stationTarget.accommodationSubsidy}
and tt.is_accommo_subsidy_arti = ${stationTarget.isAccommoSubsidyArti}

SELECT top ${pageSize} food_way, boarder_subsidies, boarder_subsidies_remark, year_month, st.station_name, dt.district_name,accommodation_subsidy FROM biz_station_target tt,biz_station st,biz_district dt
tt.station_code = st.station_code and dt.district_code = st.district_code and tt.id not in (SELECT top ${startIndex} ttt.id FROM biz_station_target ttt,biz_station st,biz_district dt
ttt.station_code = st.station_code and dt.district_code = st.district_code
and dt.district_code = '${stationTarget.districtCode}'
and ttt.year_month = '${stationTarget.yearMonth}'
and ttt.accommodation_subsidy > ${stationTarget.accommodationSubsidy}
and ttt.is_accommo_subsidy_arti = ${stationTarget.isAccommoSubsidyArti}
order by dt.district_code asc,ttt.station_code asc
)
and dt.district_code = '${stationTarget.districtCode}'
and tt.year_month = '${stationTarget.yearMonth}'
and tt.accommodation_subsidy > ${stationTarget.accommodationSubsidy}
and tt.is_accommo_subsidy_arti = ${stationTarget.isAccommoSubsidyArti}
order by dt.district_code asc,tt.station_code asc

SELECT id,station_code, year_month FROM biz_station_target where 1=1

	and year_month = #{yearMonth}


	and	station_code = #{stationCode}
